    /**
     * 查询流程节点详情
     *
     * @param id 节点ID
     * @return 节点详情
     */
    NodeEntity nodeInfo(String id);

    /**
     * 获取流程定义通过id
     *
     * @param id 流程定义id
     * @return 获取流程定义信息
     */
    WorkflowDefinitionEntity getDefinitionEntityById(String id);

    /**
     * 获取流程定义信息
     *
     * @param ids 流程ids
     * @return 流程定义列表
     */
    List<WorkflowDefinitionEntity> getDefinitionByIds(List<String> ids);
